                         .Names = c("time_decimal", "L", "M", "S", "p3", "p10", "p50", "p90", "p97"), row.names = c(NA, -194L), 
                         class = c("data.frame"))
  
  bwzout <- data.frame(size = rep(NA, length(sex)),
                       bwzfenton = rep(NA, length(sex)), 
                       bwpctilefenton = rep(NA, length(sex)))
  
  for(i in 1:nrow(bwzout)) {
    if (is.na(sex[i]) | is.na(bweight[i]) | is.na(ga[i])) {
      bwzout[i, ] = as.numeric(rep(NA, 3))
    }
    else {
      if(sex[i]==1) coef = coefboys 
      else coef = coefgirls 
      age_ditail = ga[i] - (22*7)
      if (age_ditail < 4 | age_ditail > 196) {
        bwzout[i, ] = as.numeric(rep(NA, 3))
      }
      else {
        # compute group size
        SGA = coef[coef$time_decimal==age_ditail, "p10"]
        LGA = coef[coef$time_decimal==age_ditail, "p90"]
        if (bweight[i] < SGA) {
          bwzout[i, "size"] = "SGA"
        }
        else if (bweight[i] > LGA) {
          bwzout[i, "size"] = "LGA"
          }
        else  {
          bwzout[i, "size"] = "AGA"
        }
        # compute bwzfenton
        l = coef[coef$time_decimal==age_ditail, "L"] 
        m = coef[coef$time_decimal==age_ditail, "M"] 
        s = coef[coef$time_decimal==age_ditail, "S"] 
        bwzout[i, "bwzfenton"] = ((bweight[i]/m)^(l)-1)/(l*s)
        # compute bwpctilefenton
        bwzout[i, "bwpctilefenton"] = pnorm(bwzout[i, "bwzfenton"]) * 100
      }
    }
  } 
  return(bwzout) 
}

