  blzout <- data.frame(blzfenton = rep(NA, length(sex)), 
                       blpctilefenton = rep(NA, length(sex)))
  
  for(i in 1:nrow(blzout)) {
    if (is.na(sex[i]) | is.na(blength[i]) | is.na(ga[i])) {
      blzout[i, ] = as.numeric(rep(NA, 2))
    }
    else {
      if(sex[i]==1) coef = coefboys 
      else coef = coefgirls 
      age_ditail = ga[i] - (22*7)
      if (age_ditail < 11 | age_ditail > 196) {
        blzout[i, ] = as.numeric(rep(NA, 2))
      }
      else {
        # compute blzfenton
        l = coef[coef$time_decimal==age_ditail, "L"] 
        m = coef[coef$time_decimal==age_ditail, "M"] 
        s = coef[coef$time_decimal==age_ditail, "S"] 
        blzout[i, "blzfenton"] = ((blength[i]/m)^(l)-1)/(l*s)
        # compute blpctilefenton
        blzout[i, "blpctilefenton"] = pnorm(blzout[i, "blzfenton"]) * 100
      }
    }
  } 
  return(blzout) 
}

