  bhczout <- data.frame(bhczfenton = rep(NA, length(sex)), 
                        bhcpctilefenton = rep(NA, length(sex)))
  
  for(i in 1:nrow(bhczout)) {
    if (is.na(sex[i]) | is.na(bheadcircum[i]) | is.na(ga[i])) {
      bhczout[i, ] = as.numeric(rep(NA, 2))
    }
    else {
      if(sex[i]==1) coef = coefboys 
      else coef = coefgirls 
      age_ditail = ga[i] - (22*7)
      if (age_ditail < 11 | age_ditail > 196) {
        bhczout[i, ] = as.numeric(rep(NA, 2))
      }
      else {
        # compute bhczfenton
        l = coef[coef$time_decimal==age_ditail, "L"] 
        m = coef[coef$time_decimal==age_ditail, "M"] 
        s = coef[coef$time_decimal==age_ditail, "S"] 
        bhczout[i, "bhczfenton"] = ((bheadcircum[i]/m)^(l)-1)/(l*s)
        # compute bhcpctilefenton
        bhczout[i, "bhcpctilefenton"] = pnorm(bhczout[i, "bhczfenton"]) * 100
      }
    }
  } 
  return(bhczout) 
